import React, { useEffect, useState } from "react";
import axios from "axios";


const Addcomponent = () => {


  //get all data from server
  useEffect(() => {
    document.title = "Donor Account";

  }, []);




  //creating func to post data on server
  const [item, setItem] = useState({
    title: "",
    description: "",
    quantity: "",
    weight: "",
  });
  function submit(e) {
    e.preventDefault();
    axios
      .post("/items", {
        title: item.title,
        description: item.description,
        quantity: parseInt(item.quantity),
        weight: parseInt(item.weight),
      })
      .then((res) => {
        console.log(res.item);
      });
  }

  //handle for add new item
  function handle(e) {
    const newitem = { ...item };
    newitem[e.target.id] = e.target.value;
    setItem(newitem);
    console.log(newitem);
  }


  //modal handle
  //const [show, setShow] = useState(false);
 // const handleClose = () => setShow(false);
  //const handleShow = () => setShow(true);

  //const [smShow, setSmShow] = useState(false);

  return (
              <form onSubmit={(e) => submit(e)}>
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control"
                    id="title"
                    placeholder="Enter E-Waste Name"
                    onChange={(e) => handle(e)}
                    value={item.title}
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    id="description"
                    placeholder="Enter Description"
                    onChange={(e) => handle(e)}
                    value={item.description}
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="Number"
                    className="form-control"
                    id="quantity"
                    placeholder="Enter Quantity"
                    onChange={(e) => handle(e)}
                    value={item.quantity}
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="Number"
                    className="form-control"
                    id="weight"
                    placeholder="Enter Weight"
                    onChange={(e) => handle(e)}
                    value={item.weight}
                  />
                </div>

                <button type="submit" className="btn btn-success my-4 mx-2">
                 Add Record 
                </button>
              </form>

  );
};

export default Addcomponent;
