import React, { useEffect, useState } from "react";
//import axios from "axios";
import UserService from "../services/user.service";

const Addcomponent = () => {

  //get all data from server
  useEffect(() => {
    document.title = "Donor Account";
  }, []);


  //creating func to post data on server
  const [item, setItem] = useState({
    title: "",
    description: "",
    quantity: "",
    weight: "",
  });
  function submit(e) {
    e.preventDefault();
    console.log(item.description);
    UserService.create(
      this.state.item.title,
      this.state.item.description,
      this.state.item.quantity
    ).then(
      response => {
        this.setState({
          message: response.data.message,
          successful: true
        });
      },
      error => {
        const resMessage =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();

        this.setState({
          successful: false,
          message: resMessage
        });
      }
    );
  }

  //handle for add new item
  function handle(e) {
    const newitem = { ...item };
    newitem[e.target.id] = e.target.value;
    setItem(newitem);
    console.log(newitem);
  }


  //modal handle
  //const [show, setShow] = useState(false);
 // const handleClose = () => setShow(false);
  //const handleShow = () => setShow(true);

  //const [smShow, setSmShow] = useState(false);

  return (
              <form onSubmit={(e) => submit(e)}>
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control"
                    id="title"
                    placeholder="Enter E-Waste Name"
                    onChange={(e) => handle(e)}
                    value={item.title}
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    id="description"
                    placeholder="Enter Description"
                    onChange={(e) => handle(e)}
                    value={item.description}
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="Number"
                    className="form-control"
                    id="quantity"
                    placeholder="Enter Quantity"
                    onChange={(e) => handle(e)}
                    value={item.quantity}
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="Number"
                    className="form-control"
                    id="weight"
                    placeholder="Enter Weight"
                    onChange={(e) => handle(e)}
                    value={item.weight}
                  />
                </div>

                <button type="submit" className="btn btn-success my-4 mx-2">
                 Add Record 
                </button>
              </form>

  );
};

export default Addcomponent;
