import React, { useState } from "react";
import authService from "../services/auth.service";
import userService from "../services/user.service";
import { ToastContainer, toast } from 'react-toastify';

const Addcomponent = () => {
  const currentUser = authService.getCurrentUser();

  //creating func to post data on server
  const [item, setItem] = useState({
    title: "",
    description: "",
    quantity: "",
    weight: "",
    username: currentUser.username,
  });
  function submit(e) {
    e.preventDefault();
    userService.create(item).then((response) => {
      console.log(response.item);
      toast.success(" ♻️  User Registation Successfully",{
        position:"bottom-center"
    });
    });
  }

  //handle for add new item
  function handle(e) {
    const newitem = { ...item };
    newitem[e.target.id] = e.target.value;
    setItem(newitem);
  }

  return (
    <div className="alert alert-success" role="alert" >
    <div className="register-container">
    <form onSubmit={(e) => submit(e)}>
      <div className="form-group">
        <input
          type="text"
          className="form-control"
          id="title"
          placeholder="Enter E-Waste Name"
          onChange={(e) => handle(e)}
          value={item.title} 
          required
        />
      </div>
      <div className="form-group mt-3">
        <input
          type="text"
          className="form-control"
          id="description"
          placeholder="Enter Description"
          onChange={(e) => handle(e)}
          value={item.description}
          required
        />
      </div>
      <div className="form-group mt-3">
        <input
          type="Number"
          className="form-control"
          id="quantity"
          placeholder="Enter Quantity"
          onChange={(e) => handle(e)}
          value={item.quantity}
          required
        />
      </div>
      <div className="form-group mt-3">
        <input
          type="Number"
          className="form-control"
          id="weight"
          placeholder="Enter Weight"
          onChange={(e) => handle(e)}
          value={item.weight}
          required
        />
      </div>

      <button
        type="submit"
        className="btn btn-success my-4 mx-2"
      >
        Add Record
      </button>
      
    </form>
    <ToastContainer />
        </div></div>
      
  );
};

export default Addcomponent;
