import React, { Component } from "react";
import Link  from "react-router-dom";
import "../../App.css";
import companyLogo from "../../images/logo.gif";
import EventBus from "../../common/EventBus";
import AuthService from "../../services/auth.service";


class Navbar extends Component {
  constructor(props) {
    super(props);
    this.logOut = this.logOut.bind(this);

    this.state = {
      showModeratorBoard: false,
      showAdminBoard: false,
      currentUser: undefined,
    };
  }

  componentDidMount() {
    const user = AuthService.getCurrentUser();

    if (user) {
      this.setState({
        currentUser: user,
        showModeratorBoard: user.roles.includes("ROLE_MODERATOR"),
        showAdminBoard: user.roles.includes("ROLE_ADMIN"),
      });
    }

    EventBus.on("logout", () => {
      this.logOut();
    });
  }

  componentWillUnmount() {
    EventBus.remove("logout");
  }

  logOut() {
    AuthService.logout();
    this.setState({
      showModeratorBoard: false,
      showAdminBoard: false,
      currentUser: undefined,
    });
  }

  render() {
    const { currentUser, showAdminBoard } = this.state;

    return (
      <>
        <nav class="navbar navbar-expand-lg navbar-light bg-dark">
          <Link to="/">
            <img src={companyLogo} alt="logo" height={125} />
          </Link>
          <Link to={"/"}>
            <h3>E-Waste Collection</h3>
          </Link>
          {currentUser ? (
            
            <div>
              <li>
                <Link to={"/home"} class="btn btn-outline-success">
                  HOME
                </Link>
              </li>
              <li>
                <Link to={"/profile"} class="btn btn-outline-success">
                  {currentUser.username}
                </Link>
              </li>
              {showAdminBoard && (
                <li>
                  <Link to={"/admin"} class="btn btn-outline-success">
                    ADMIN BOARD
                  </Link>
                </li>
              )}
              {currentUser && (
                <li>
                  <button type="button">
                    <Link to={"/user"}>DASHBOARD</Link>
                  </button>
                </li>
              )}
              <li>
                <button type="button">
                  <Link to={"/certificate"}>CERTIFICATE</Link>
                </button>
              </li>
              <li>
                <button type="button">
                  <Link to={"/login"} onClick={this.logOut}>
                    LOGOUT
                  </Link>
                </button>
              </li>
            </div>
          ) : (
            <div class="collapse navbar-collapse" id="navbarSupportedContent">
            <ul class="navbar-nav me-auto mb-2 mb-lg-0">
              <li class="nav-item">
                <Link to={"/home"} class="btn btn-outline-success">
                  HOME
                </Link>
              </li>
              <li class="nav-item">
                <Link to={"/login"} class="btn btn-outline-success">
                  LOGIN
                </Link>
              </li>
              <li class="nav-item">
                <Link to={"/register"} class="btn btn-outline-success">
                  SIGN UP
                </Link>
              </li>
              <li class="nav-item">
                <Link to={"/team"} class="btn btn-outline-success">
                  MEET TEAM
                </Link>
              </li>
            </ul>
            </div>
          )}
        </nav>
      </>
    );
  }
}

export default Navbar;
