import "bootstrap/dist/css/bootstrap.min.css";
import { Button, Modal } from "react-bootstrap";
import React, { useEffect, useState } from "react";
import axios from "axios";


const AllEwaste = () => {
  const [items, setItems] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState("");
  const [weight, setWeight] = useState("");
  const [itemId, setItemId]= useState("")

  //get all data from server
  useEffect(() => {
    document.title = "Donor Account";
 
    getallitemsfromserver();
  }, []);

  function getallitemsfromserver () {
    fetch("/items").then((result) => {
      result.json().then((response) => {
        setItems(response);
        setTitle(response[0].title)
        setDescription(response[0].description)
        setQuantity(response[0].quantity)
        setWeight(response[0].weight)
      });
    });
  };



  //creating func to post data on server
  const [item, setItem] = useState({
    title: "",
    description: "",
    quantity: "",
    weight: "",
  });
  function submit(e) {
    e.preventDefault();
    axios
      .post("/items", {
        title: item.title,
        description: item.description,
        quantity: parseInt(item.quantity),
        weight: parseInt(item.weight),
      })
      .then((res) => {
        console.log(res.item);
      });
  }

  //handle for add new item
  function handle(e) {
    const newitem = { ...item };
    newitem[e.target.id] = e.target.value;
    setItem(newitem);
    console.log(newitem);
  }

  //Delete Function
  const deleteitem = (id) => {
    axios.delete(`/items/${id}`).then(
      (response) => {},
      (error) => {}
    );
    getallitemsfromserver();
  };
  
  //edit Function
  const selectitem = (getitems) => {
    setTitle(getitems.title);
    setDescription(getitems.description);
    setQuantity(getitems.quantity);
    setWeight(getitems.weight);
    setItemId(getitems.id)
  };

function updateRecord(){
  let item = {title,description,quantity,weight,itemId}
  console.warn("item",item)
  fetch(`/items/${itemId}`,{
    method: 'PUT',
    headers:{
      'Accept':'application/json',
      'Content-Type':'application/json'
    },
    body:JSON.stringify(item)
  }).then((result)=>{
    result.json().then((response)=>{
      console.log(response)
    })
  });
}

  //modal handle
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [smShow, setSmShow] = useState(false);

  return (
    <div className="container ">
      <div className="crud shadow-lg p-3 mb-5 mt-5 bg-body rounded">
        <div className="row ">
          <div class="col-sm-3 mt-5 mb-4 text-gred">
            <div className="search">
              <form class="form-inline">
                <input
                  class="form-control mr-sm-2"
                  type="search"
                  placeholder="Search Student"
                  aria-label="Search"
                />
              </form>
            </div>
          </div>
          <div
            className="col-sm-3 offset-sm-2 mt-5 mb-4 text-gred"
            style={{ color: "#198754" }}
          >
            <h2>
              <b>Ewaste Details</b>
            </h2>
          </div>
          <div className="col-sm-3 offset-sm-1  mt-5 mb-4 text-gred">
            <Button variant="primary" onClick={handleShow}>
              Add New Entry
            </Button>
          </div>
        </div>
        <div className="row">
          <div className="table-responsive ">
            <table className="table table-striped table-hover table-bordered">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>E-Name</th>
                  <th>E-Description</th>
                  <th>Quantity</th>
                  <th>Weight</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {items.map((getitems) => (
                  <tr>
                    <td>{getitems.id}</td>
                    <td>{getitems.title}</td>
                    <td>{getitems.description}</td>
                    <td>{getitems.quantity}</td>
                    <td>{getitems.weight}</td>
                    <td>
                      {/*<i style={{ color: "#10ab80" }} className="material-icons">&#xE417;</i>*/}
                      <button
                        className="btn btn-light"
                        onClick={() => setSmShow(true)}
                      >
                        <i
                          class="btn btn-light"
                          style={{ color: "#198754" }}
                          className="material-icons"
                          onClick={() => {
                            selectitem(getitems);
                          }}
                        >
                          &#xE254;
                        </i>
                      </button>
                      <a href="/donarHome">
                        <button className="btn btn-light">
                          <i
                            style={{ color: "red" }}
                            onClick={() => {
                              deleteitem(getitems.id);
                            }}
                            className="material-icons"
                          >
                            &#xE872;
                          </i>
                        </button>
                      </a>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>

        {/* <!---New Entry Model Box ---> */}
        <div className="model_box">
          <Modal
            show={show}
            onHide={handleClose}
            backdrop="static"
            keyboard={true}
          >
            <Modal.Header closeButton>
              <Modal.Title>Add New Entry</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <form onSubmit={(e) => submit(e)}>
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control"
                    id="title"
                    placeholder="Enter E-Waste Name"
                    onChange={(e) => handle(e)}
                    value={item.title}
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    id="description"
                    placeholder="Enter Description"
                    onChange={(e) => handle(e)}
                    value={item.description}
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="Number"
                    className="form-control"
                    id="quantity"
                    placeholder="Enter Quantity"
                    onChange={(e) => handle(e)}
                    value={item.quantity}
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="Number"
                    className="form-control"
                    id="weight"
                    placeholder="Enter Weight"
                    onChange={(e) => handle(e)}
                    value={item.weight}
                  />
                </div>

                <Button type="submit" className="btn btn-success my-4 mx-2" onClick={handleClose}>
                 Add Record 
                </Button>
              </form>
            </Modal.Body>

            <Modal.Footer>
              <Button variant="secondary" onClick={handleClose}>
                Close
              </Button>
            </Modal.Footer>
          </Modal>
        </div>
        {/* Model Box Finsihs */}

        {/* Ewaste Edit Modal*/}
        <Modal show={smShow} onHide={() => setSmShow(false)}>
          <Modal.Header closeButton>
            <Modal.Title>Edit Record</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            
              <form >
                <div className="form-group">
                  <input
                    type="text"
                    className="form-control"
                    value={title}
                    onChange={(e)=>setTitle(e.target.value)}
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="text"
                    className="form-control"
                    value={description}
                    onChange={(e)=>setDescription(e.target.value)}
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="Number"
                    className="form-control"
                    value={quantity}
                    onChange={(e)=>setQuantity(e.target.value)}
                  />
                </div>
                <div className="form-group mt-3">
                  <input
                    type="Number"
                    className="form-control"
                    value={weight}
                    onChange={(e)=>setWeight(e.target.value)}
                  />
                </div>
                <Button type="submit" className="btn btn-success my-4 mx-2" onClick={updateRecord}>
                  Update Record
                </Button>
              </form>
          
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={() => setSmShow(false)}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    </div>
  );
};

export default AllEwaste;
