import React, { useEffect, useState } from "react";
import { Button, Modal } from "react-bootstrap";
import authService from "../services/auth.service";
import UserService from "../services/user.service";
import BoardUser from "./board-user.component";
//import EventBus from "../common/EventBus";

const Addcomponent = () => {
  const currentUser = authService.getCurrentUser();
  const [show, setShow] = useState(false);
  const [currentDetails, setcurrentDetails] = useState([]);
  const getallrecords = () => {
    const currentUser = authService.getCurrentUser();
    UserService.getUserBoard(currentUser.id).then((response) => {
      setcurrentDetails(response.data);
    });
  };

  //useEffect(() => {
  //  getallrecords();
  // }, []);

  //creating func to post data on server
  const [item, setItem] = useState({
    title: "",
    date: "",
    quantity: "",
    weight: "",
    usernameid: currentUser.id,
  });
  function submit(e) {
    e.preventDefault();
    UserService.create(item).then((response) => {
      //console.log(response.item);
      setShow(false);
      getallrecords();
    });
  }

  //handle for add new item
  function handle(e) {
    const newitem = { ...item };
    newitem[e.target.id] = e.target.value;
    setItem(newitem);
  }

  return (
    <Modal show={show} onHide={() => setShow(true)}>
      <Modal.Header closeButton>
        <Modal.Title>Add Record</Modal.Title>
      </Modal.Header>
      <Modal.Body className="p-2">
        <div className="alert alert-success p-1" role="alert">
          <div className="register-container p-1">
            <form onSubmit={(e) => submit(e)}>
              <label htmlFor="username">E Waste Name</label>
              <div className="form-group">
                <input
                  type="text"
                  className="form-control"
                  id="title"
                  placeholder="Enter E-Waste Name"
                  onChange={(e) => handle(e)}
                  value={item.title}
                  required
                />
              </div>
              <div className="form-group ">
                <label htmlFor="username">Pick-Up Date</label>
                <input
                  type="date"
                  className="form-control"
                  id="date"
                  placeholder="Enter Pick Up Date"
                  onChange={(e) => handle(e)}
                  value={item.date}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="username">Quantity</label>
                <input
                  type="Number"
                  className="form-control"
                  id="quantity"
                  placeholder="Enter Quantity"
                  onChange={(e) => handle(e)}
                  value={item.quantity}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="username">Weight</label>
                <input
                  type="Number"
                  className="form-control"
                  id="weight"
                  placeholder="Enter Weight in grams"
                  onChange={(e) => handle(e)}
                  value={item.weight}
                  required
                />
              </div>
              <button type="submit" className="btn btn-success my-1 mx-1">
                Add Record
              </button>
            </form>
          </div>
        </div>
      </Modal.Body>
      <Modal.Footer className="p-1">
        <Button variant="secondary" onClick={() => setShow(false)}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default Addcomponent;
